data.template:
conf t

snmp-server community {{snmpCommunity}} RO 

! if using a specific netflow records
! else use netflow-original which comes by default
flow record {{customFlowRecord}}
    match ipv4 tos
    match ipv4 protocol
    match ipv4 source address
    match ipv4 destination address
    match transport source-port
    match transport destination-port
    match interface input
    collect routing source as
    collect routing destination as
    collect routing next-hop address ipv4
    collect transport tcp flags
    collect interface output
    collect counter bytes
    collect counter packets
    collect timestamp sys-uptime first
    collect timestamp sys-uptime last
!
sampler {{netflowSampler}}
    mode random 1 out-of {{deviceSampleRate+1}}
!
flow exporter {{exporter}}
    ! Direct export to Kentik Flow Ingest (without Flow Proxy)
    description {{publicFlowIngestDescription}}
    destination {{kentikIngestIP}} {% if vrfName %}vrf {{vrfName}}{% endif %} 
    source {{sourceInterface}}
    transport udp {{kentikIngestUDPPort}}
    version 9
!
!
flow monitor {{flowMonitor}}
    exporter {{exporter}}
    cache timeout inactive 10
    cache timeout active 60
    ! if using custom Netflow records
    record {{customFlowRecord}}
    ! if using standard default Netflow records
    record netflow-original
exit
{% for i in sourceInterfaces %}
interface {{i}}
    ip flow monitor {{flowMonitor}} input
{% endfor %}

{% if vrfName %} 
! Add a route to Kentik Ingest IP if in the <vrfName> in case it has no route to it
ip route vrf {{vrfName}} {{kentikIngestIP}} 255.255.255.255 {{vrfGatewayIP}}
{% endif %} 

exit
